here so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='7' ) realprice7, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='8' ) count8, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='8' ) realprice8, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='9' ) count9, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='9' ) realprice9, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='10' ) count10, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='10' ) realprice10, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='11' ) count11, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='11' ) realprice11, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='12' ) count12, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') and month(so.ordertime)='12' ) realprice12, (select count(*) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') ) count, (select sum(so.realprice) from shop_order so where so.shopid=si.shopid and so.`status` in ('2','3') ) realprice from shop_info si join shop_order sor on sor.shopid=si.shopid where si.`status` in ('3','4') and si.isdelete='1'
