mapper.select:
select advertid,advertname,provinceid,cityid,countyid,ordernum,imageurl, detailpage,createtime,creatorid,status,memo, (select areaname from dic_area n where m.provinceid=n.areaid) as provincename, (select areaname from dic_area n where m.cityid=n.areaid) as cityname, (select areaname from dic_area n where m.countyid=n.areaid) as countyname from advert_info m where 1=1
and advertname = #{advertname}
and status = #{status}
order by createtime desc,provinceid,cityid

mapper.update:
		update advert_info set  advertname=#{advertname},provinceid=#{provinceid},cityid=#{cityid},
  		countyid=#{countyid},ordernum=#{ordernum},imageurl=#{imageurl},detailpage=#{detailpage},
   		status=#{status},memo=#{memo} where advertid =#{advertid}
