mapper.select:
select couponid,couponcode,couponname,coupondesc,begintime,endtime,coupontype,couponamount,couponnums, fullamount,shopid,createtime,creatorid,status,memo, (select shopname from shop_info n where m.shopid=n.shopid) as shopname from coupon_info m where 1=1
and status = #{status}
and shopid = #{shopid}
order by begintime desc
